<script>
import ChartDataLabels from 'chartjs-plugin-datalabels';

import {
    Chart as ChartJS,
    Title,
    Tooltip,
    Legend,
    BarElement,
    CategoryScale,
    LinearScale,
    ArcElement
} from 'chart.js'
import { Bar, Doughnut } from 'vue-chartjs'

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement, ChartDataLabels)

export default {

    props: {
        locales: { default: null },
        pageTitle: { default: null, String },
    },

    components: {
        Bar,
        Doughnut
    },

    data() {
        return {
            dataBar: {
                labels: ['E10', 'E11', 'E12', 'E13', 'E14', 'E15', 'E16', 'E17', 'E18', 'E19'],
                data: [110, 70, 70, 90, 55, 30, 90, 115, 90, 95],
            },

            dataDoughnutSex: {
                labels: ['Nữ', 'Nam'],
                datasets: [
                    {
                        backgroundColor: ['#1790C9', '#119DB5'],
                        data: [44, 56],
                        borderJoinStyle: 'round',
                        weight: 250
                    }
                ]
            },
            dataDoughnutAge: {
                labels: ['0-6', '7-12', '13-18', '19-40', '41-60', 'Trên 60'],
                datasets: [
                    {
                        backgroundColor: ['#E17126', '#617882', '#119DB5', '#015E99', '#1790C9', '#FFC000'],
                        data: [2, 14, 28, 37, 14, 5],
                        borderJoinStyle: 'round',
                        weight: 250
                    }
                ]
            },
            optionsDoughnut: {
                responsive: true,
                tooltips: {
                    enabled: false,
                },
                plugins: {
                    legend: {
                        display: true,
                        position: 'right',
                        labels: {
                            usePointStyle: true,
                            useBorderRadius: false,
                            borderRadius: 50,
                            padding: 20,
                        },
                    },
                    datalabels: {
                        display: 'true',
                        color: 'black',
                        font: {
                            size: 14,
                            // weight: '500'
                        },

                        formatter: (value) => {
                            return value + '%';
                        },
                    }
                },


            },

            optionsBar: {
                responsive: true,
                maintainAspectRatio: false
            }
        }
    },
    methods: {

        renderChart() {
            // Get the canvas element
            const canvas = this.$refs.barChart;
            const ctx = canvas.getContext('2d');

            // Create a linear gradient
            const gradient = ctx.createLinearGradient(0, 0, 0, 300);
            gradient.addColorStop(0, '#1976CC');
            gradient.addColorStop(1, '#0FABCD');

            // Dummy data for demonstration
            const data = {
                labels: this.dataBar.labels,
                datasets: [{
                    label: 'Sample Data',
                    data: this.dataBar.data,
                    backgroundColor: gradient, // Use the linear gradient as the background color
                    borderColor: 'rgba(75, 192, 192, 1)',
                    borderWidth: 1,
                    barPercentage: 0.5,
                }]
            };

            // Chart configuration
            const options = {
                maintainAspectRatio: false,
                responsive: true,

                scales: {
                    y: {
                        beginAtZero: true
                    },
                    x: {
                        barThickness: 40, // Điều chỉnh độ cao của thanh
                    },
                },
                plugins: {
                    legend: {
                        display: false,
                    },
                    datalabels: {
                        display: false,
                    }
                }
            };

            // Create the bar chart
            new ChartJS(ctx, {
                type: 'bar',
                data: data,
                options: options
            });
        }


    },
    mounted() {

        this.renderChart();

    },
    computed: {}
}
</script>

